<script>
import axios from "axios";
export default {
  name: "app",
  data() {
    return {
      city: "istanbul",
      weather: {},
    };
  },
  methods: {
    getData() {
      axios
        .get(
          `https://api.openweathermap.org/data/2.5/weather?q=${this.city}&units=metric&appid=1b82211d9eae6d8964717fe8ecb5cc33`
        )
        .then((response) => {
          console.log(response.data);
          console.log(response.data.weather[0].main);
          this.weather = response.data;
        })
        .catch((error) => console.log({ error }));
    },
  },
  created() {
    this.getData();
  },
};
</script>

